def _parse_image_ref(image_href: str) -> Tuple[str, str, bool]:
    image_href = urlparse(image_href)
    if not image_href.scheme:
        raise ValueError("Image href must have scheme")
    if not image_href.netloc:
        raise ValueError("Image href must have netloc")
    if not image_href.path.startswith('/'):
        raise ValueError("Image href must have path starting with /")
    return image_href.path, image_href.netloc, image_href.scheme

def _parse_image_ref(image_href: str) -> Tuple[str, str, bool]:
    if not image_href:
        raise ValueError("Empty image reference")
    url = urlparse(image_href)
    if url.netloc:
        netloc = url.netloc
    else:
        netloc = url.path.split(':')[0]
    use_ssl = url.scheme in ('http', 'https')
    return url.path, netloc, use_ssl

def _parse_image_ref(image_href: str) -> Tuple[str, str, bool]:
    try:
        image_id = urllib.parse.urlparse(image_href).netloc
    except ValueError:
        raise ValueError("invalid image href: %s" % image_href)
    image_id = image_id.split(":")[0]
    netloc = image_id.split("/")[1]
    use_ssl = image_id.startswith("http")
    return image_id, netloc, use_ssl

